const express = require('express');
const multer = require('multer');
const multerS3 = require('multer-s3');
const { S3Client } = require('@aws-sdk/client-s3');
const s3 = new S3Client({});
const upload = multer({ storage: multerS3({ s3, bucket: '{{bucket}}', metadata: (_req, file, cb) => cb(null, { fieldName: file.fieldname }), key: (_req, file, cb) => cb(null, file.originalname) }) });
const app = express();
app.post('/upload', upload.single('file'), (req, res) => res.json({ key: req.file.key }));
app.listen(Number('{{port}}'), () => console.log('listening on {{port}}'));
