import neo4j from 'neo4j-driver';
const d = neo4j.driver(process.env.NEO4J_URI, neo4j.auth.basic(process.env.NEO4J_USER, process.env.NEO4J_PASS));
const s = d.session();
try { const r = await s.run('RETURN 1 as x'); console.log(r.records[0].get('x')); } finally { await s.close(); await d.close(); }
