import * as React from 'react';
const items = ['Apple','Banana','Cherry'];
export function Combobox(){ const [q,setQ]=React.useState(''); const [open,setOpen]=React.useState(false); const list = items.filter(i=>i.toLowerCase().includes(q.toLowerCase())); return (<div className='relative inline-block'><input className='border px-2 py-1' value={q} onChange={e=>setQ(e.target.value)} onFocus={()=>setOpen(true)} placeholder='Search...' />{open&&(<div className='absolute left-0 right-0 bg-white border shadow'>{list.map(i=>(<div key={i} className='px-2 py-1 hover:bg-gray-100' onMouseDown={()=>{setQ(i); setOpen(false);}}>{i}</div>))}</div>)}</div>); }
