import * as React from 'react';
const items = ['Open', 'Save', 'Close'];
export function Command(){ const [q,setQ]=React.useState(''); const list = items.filter(i=>i.toLowerCase().includes(q.toLowerCase())); return (<div className='w-80 border rounded p-2'><input className='w-full outline-none' placeholder='Type a command' value={q} onChange={e=>setQ(e.target.value)} />{q && (<div className='mt-2'>{list.map(i=>(<div key={i} className='px-2 py-1 hover:bg-gray-100 cursor-pointer' onMouseDown={()=>setQ(i)}>{i}</div>))}</div>)}</div>); }
