import NextAuth from 'next-auth';
import Credentials from 'next-auth/providers/credentials';
import bcrypt from 'bcryptjs';
const users = [{ id:'1', email:'demo@example.com', hash: bcrypt.hashSync('password', 8) }];
const handler = NextAuth({ providers:[ Credentials({ name:'Credentials', credentials:{ email:{ label:'Email', type:'text' }, password:{ label:'Password', type:'password' } }, async authorize(creds){ const u = users.find(x=>x.email===creds?.email); if(u && bcrypt.compareSync(creds!.password!, u.hash)) return { id:u.id, email:u.email }; return null; } }) ] });
export { handler as GET, handler as POST };
