import { z } from 'zod';
const schema = z.object({ name: z.string() });
export async function POST(req: Request){ const body = await req.json(); const r = schema.safeParse(body); if(!r.success) return new Response('bad', { status: 400 }); return Response.json({ ok: true, name: r.data.name }); }
