'use client';
import { datadogRum } from '@datadog/browser-rum';
import { useEffect } from 'react';
export default function Providers({ children }: { children: React.ReactNode }){ useEffect(()=>{ datadogRum.init({ applicationId: process.env.NEXT_PUBLIC_DD_RUM_APP_ID||'', clientToken: process.env.NEXT_PUBLIC_DD_RUM_CLIENT_TOKEN||'', site: 'datadoghq.com', service: 'app', env: 'dev', version: '0.0.1', sampleRate: 100, trackInteractions: true }); },[]); return <>{children}</>; }
