'use client';
import React, { useEffect, useState } from 'react';
import Lottie from 'lottie-react';

export default function Page(){
  const [data, setData] = useState<any | null>(null);
  const url = process.env.NEXT_PUBLIC_LOTTIE_URL || '{{lottie_url}}';

  useEffect(() => {
    let alive = true;
    fetch(url).then(r => r.json()).then(json => { if (alive) setData(json); });
    return () => { alive = false; };
  }, [url]);

  return (
    <main style={{ fontFamily: 'sans-serif', padding: 24 }}>
      <h1>Lottie in Next.js (Client Component)</h1>
      <div style={{ width: 240, height: 240, border: '1px solid #ddd', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {data ? <Lottie animationData={data} loop autoplay style={{ width: 240, height: 240 }} /> : 'Loading...'}
      </div>
    </main>
  );
}
