'use client';
import React, { useEffect, useRef, useState } from 'react';
import Lottie, { LottieRefCurrentProps } from 'lottie-react';

export default function Page(){
  const lottieRef = useRef<LottieRefCurrentProps>(null);
  const [data, setData] = useState<any | null>(null);
  const url = process.env.NEXT_PUBLIC_LOTTIE_URL || '{{lottie_url}}';

  useEffect(() => {
    let alive = true;
    fetch(url).then(r => r.json()).then(json => { if (alive) setData(json); });
    return () => { alive = false; };
  }, [url]);

  useEffect(() => {
    let lastY = window.scrollY;
    let lastT = performance.now();
    let raf = 0;
    const clamp = (v:number, min:number, max:number) => Math.max(min, Math.min(max, v));
    const onScroll = () => {
      cancelAnimationFrame(raf);
      raf = requestAnimationFrame(() => {
        const nowY = window.scrollY;
        const nowT = performance.now();
        const dy = Math.abs(nowY - lastY);
        const dt = Math.max(1, nowT - lastT);
        const pxPerMs = dy / dt;
        const speed = clamp(pxPerMs * 8, 0.2, 3); // スケール係数は経験値
        (lottieRef.current as any)?.setSpeed?.(speed);
        lastY = nowY;
        lastT = nowT;
      });
    };
    window.addEventListener('scroll', onScroll, { passive: true });
    return () => { cancelAnimationFrame(raf); window.removeEventListener('scroll', onScroll as any); };
  }, []);

  return (
    <main style={{ fontFamily: 'sans-serif' }}>
      <div style={{ height: '120vh', display: 'grid', placeItems: 'center' }}><h1>Scroll to speed up</h1></div>
      <section style={{ minHeight: '80vh', display: 'grid', placeItems: 'center' }}>
        <div style={{ width: 320, height: 320, border: '1px solid #ddd', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          {data ? (
            <Lottie lottieRef={lottieRef} animationData={data} loop autoplay style={{ width: 320, height: 320 }} />
          ) : 'Loading...'}
        </div>
        <p style={{ opacity: 0.6, marginTop: 12 }}>スクロールが速いほど再生速度が上がります</p>
      </section>
      <div style={{ height: '120vh' }} />
    </main>
  );
}
