import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
export function middleware(req: NextRequest){ const cookie = req.cookies.get('variant')?.value || (Math.random()<0.5?'A':'B'); const res = NextResponse.next(); res.cookies.set('variant', cookie, { path:'/' }); res.headers.set('x-variant', cookie); return res; }
export const config = { matcher: '/:path*' };
