import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
import { Redis } from '@upstash/redis'; const kv = Redis.fromEnv();
export async function middleware(req: NextRequest){ const on = await kv.get<boolean>('{{flag_key}}'); if(on!==true && req.nextUrl.pathname.startsWith('/new-ui')){ return NextResponse.rewrite(new URL('/disabled', req.url)); } return NextResponse.next(); }
export const config = { matcher: '/:path*' };
