import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
export function middleware(req: NextRequest){ const c = req.geo?.country || 'UN'; if(c==='{{country}}' && req.nextUrl.pathname==='/'){ const url = req.nextUrl.clone(); url.pathname = '/ja/'; return NextResponse.redirect(url); } return NextResponse.next(); }
export const config = { matcher: '/:path*' };
