import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
export function middleware(req: NextRequest){ const res = NextResponse.next(); res.headers.set('strict-transport-security', `max-age={{max_age}}; includeSubDomains; preload`); return res; }
export const config = { matcher: '/:path*' };
