import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
const SUPPORTED = ['en','ja'];
export function middleware(req: NextRequest){ const { pathname } = req.nextUrl; if(pathname==='/' || pathname===''){ const al = req.headers.get('accept-language')||''; const cand = al.split(',')[0].split('-')[0]; const locale = SUPPORTED.includes(cand)?cand:'{{default_locale}}'; const url = req.nextUrl.clone(); url.pathname = '/' + locale + '/'; return NextResponse.redirect(url); } return NextResponse.next(); }
export const config = { matcher: '/:path*' };
