import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
export function middleware(req: NextRequest){ if(req.nextUrl.pathname.startsWith('/legacy')){ const url = req.nextUrl.clone(); url.pathname = '/'; return NextResponse.redirect(url, 307); } return NextResponse.next(); }
export const config = { matcher: '/:path*' };
