import { NextResponse } from 'next/server';
export const config = { matcher: ['/admin/:path*'] };
export function middleware(req: Request){ const url = new URL(req.url); const role = url.searchParams.get('role') || 'user'; if(role !== 'admin'){ return NextResponse.redirect(new URL('/403', url)); } return NextResponse.next(); }
