import { NextResponse } from 'next/server'; import type { NextRequest } from 'next/server';
export function middleware(req: NextRequest){ const res = NextResponse.next(); res.headers.set('x-frame-options','DENY'); res.headers.set('x-content-type-options','nosniff'); res.headers.set('referrer-policy','no-referrer'); res.headers.set('content-security-policy', `{{csp}}`); return res; }
export const config = { matcher: '/:path*' };
