import { S3Client, UploadPartCommand } from '@aws-sdk/client-s3';
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
export async function GET(req: Request){ const { searchParams } = new URL(req.url); const uploadId = searchParams.get('uploadId')!; const partNumber = Number(searchParams.get('partNumber')||'1'); const s3 = new S3Client({}); const url = await getSignedUrl(s3, new UploadPartCommand({ Bucket: process.env.S3_BUCKET!, Key: 'big.bin', UploadId: uploadId, PartNumber: partNumber }), { expiresIn: 300 }); return Response.json({ url }); }
