export const runtime = 'nodejs';
import { trace } from '@opentelemetry/api';
export async function GET(){ const tracer = trace.getTracer('next-route'); const result = await tracer.startActiveSpan('work', async (span)=>{ await new Promise(r=>setTimeout(r,50)); span.end(); return 'ok'; }); return Response.json({ result }); }
