const headers = { 'access-control-allow-origin': '{{origin}}', 'access-control-allow-headers':'content-type, authorization', 'access-control-allow-methods':'GET,POST,PUT,DELETE,OPTIONS' };
export async function OPTIONS(){ return new Response(null, { headers }); }
export async function GET(){ return new Response('ok', { headers }); }
export async function POST(){ return new Response('ok', { headers }); }
