'use server';
import { z } from 'zod';
const Schema = z.object({ email: z.string().email() });
export async function save(formData: FormData){ const data = { email: String(formData.get('email')||'') }; const v = Schema.safeParse(data); if(!v.success) return { ok:false, error:'invalid' }; return { ok:true }; }
