'use server';
import { z } from 'zod';
export type Result<T> = { ok: true; data: T } | { ok: false; error: string };
const Schema = z.object({ email: z.string().email() });
export async function save(input: unknown): Promise<Result<{ email: string }>>{ try { const v = Schema.parse(input); return { ok: true, data: v }; } catch{ return { ok: false, error: 'invalid' }; } }
