'use server';
import { z } from 'zod';
export type Result<T> = { ok: true; data: T } | { ok: false; errors: any };
const Schema = z.object({ email: z.string().email(), name: z.string().min(1) });
export async function save(data: unknown): Promise<Result<{ email:string; name:string }>>{ const v = Schema.safeParse(data); if(!v.success){ return { ok:false, errors: v.error.flatten() }; } return { ok:true, data: v.data }; }
