import { S3Client, GetObjectCommand } from '@aws-sdk/client-s3';
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
export async function GET(){ const s3 = new S3Client({}); const url = await getSignedUrl(s3, new GetObjectCommand({ Bucket: process.env.S3_BUCKET!, Key: 'download.bin' }), { expiresIn: 300 }); return Response.json({ url }); }
