import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
export async function GET(){ const s3 = new S3Client({}); const url = await getSignedUrl(s3, new PutObjectCommand({ Bucket: process.env.S3_BUCKET!, Key: 'upload.bin', ContentType: 'application/octet-stream' }), { expiresIn: 300 }); return Response.json({ url }); }
