import { NextResponse } from 'next/server';
import Stripe from 'stripe';
export async function POST(){ const stripe = new Stripe(process.env.STRIPE_API_KEY || '', { apiVersion: '2024-06-20' as any }); const session = await stripe.billingPortal.sessions.create({ customer: '{{customer_id}}', return_url: 'https://example.com/account' }); return NextResponse.json({ url: session.url }); }
