import { NextResponse } from 'next/server';
import Stripe from 'stripe';
export async function POST(){ const stripe = new Stripe(process.env.STRIPE_API_KEY || '', { apiVersion: '2024-06-20' as any }); const session = await stripe.checkout.sessions.create({ mode: 'payment', line_items: [{ price: '{{price_id}}', quantity: 1 }], success_url: 'https://example.com/success', cancel_url: 'https://example.com/cancel' }); return NextResponse.json({ url: session.url }); }
