import { supabase } from '../../lib/supabase';
export default function Page(){
  async function login(formData: FormData){ 'use server'; const email=String(formData.get('email')||''); const password=String(formData.get('password')||''); await supabase.auth.signInWithPassword({ email, password }); }
  return (<form action={login}><input name='email' placeholder='email' /><input name='password' type='password' placeholder='password' /><button>Login</button></form>); }
