import { Kafka } from 'kafkajs';
const kafka = new Kafka({ clientId: 'demo', brokers: ['localhost:9092'] });
const p = kafka.producer();
await p.connect();
await p.send({ topic: 'demo', messages: [{ value: 'hello' }] });
await p.disconnect();

import { Kafka } from 'kafkajs';
const kafka = new Kafka({ clientId: 'demo', brokers: ['localhost:9092'] });
const c = kafka.consumer({ groupId: 'demo-group' });
await c.connect();
await c.subscribe({ topic: 'demo' });
await c.run({ eachMessage: async ({ message }) => console.log('msg', message.value?.toString()) });
