import { logs, metrics } from '@opentelemetry/api';
import { MeterProvider } from '@opentelemetry/sdk-metrics';
import { LoggerProvider } from '@opentelemetry/sdk-logs';
import { OTLPMetricExporter } from '@opentelemetry/exporter-metrics-otlp-http';
import { OTLPLogExporter } from '@opentelemetry/exporter-logs-otlp-http';
const meterProvider = new MeterProvider({});
meterProvider.addMetricReader(new (class { constructor(){ this.exporter=new OTLPMetricExporter(); } async collect(){} })());
const counter = meterProvider.getMeter('demo').createCounter('requests'); counter.add(1);
const loggerProvider = new LoggerProvider();
const logger = loggerProvider.getLogger('demo');
logger.emit({ body: 'started' });
console.log('otel emitted');
