const { Worker } = require('worker_threads');
function run(task) { return new Promise((res) => { const w = new Worker('module.exports=parentPort.on('message', n=>{parentPort.postMessage(n*n)})', { eval: true }); w.on('message', res); w.postMessage(task); }); }
(async()=>{ console.log(await Promise.all([1,2,3].map(run))); })();
