const nodemailer = require('nodemailer');
const { SESClient } = require('@aws-sdk/client-ses');
const ses = new SESClient({});
(async () => {
  const transport = nodemailer.createTransport({ SES: { ses, aws: { SendEmailCommand: undefined } } });
  const info = await transport.sendMail({ from: '{{from}}', to: '{{to}}', subject: 'Hello', text: 'Hello from SES' });
  console.log('Message sent', info.messageId);
})();
