const res = await fetch('{{ollama_url}}/api/embeddings', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ model: process.env.OLLAMA_EMBED_MODEL || '{{embed_model}}', prompt: 'hello world' }) });
const json = await res.json();
console.log(Array.isArray(json?.embedding) ? json.embedding.length : json);
