const res = await fetch('{{ollama_url}}/api/chat', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    model: '{{ollama_model}}',
    messages: [{ role: 'user', content: 'Say hello' }],
    stream: false
  })
});
const json = await res.json();
console.log(json.message?.content || JSON.stringify(json));
