// Create assistant, thread, and run via REST
const headers = { 'Authorization': `Bearer ${process.env.OPENAI_API_KEY}`, 'Content-Type': 'application/json' };
const a = await fetch('https://api.openai.com/v1/assistants', { method: 'POST', headers, body: JSON.stringify({ name: 'helper', model: 'gpt-4o-mini' }) }).then(r=>r.json());
const t = await fetch('https://api.openai.com/v1/threads', { method: 'POST', headers }).then(r=>r.json());
const run = await fetch(`https://api.openai.com/v1/threads/${t.id}/runs`, { method: 'POST', headers, body: JSON.stringify({ assistant_id: a.id }) }).then(r=>r.json());
console.log(run.id);
