import OpenAI from 'openai';
import fs from 'node:fs';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const resp = await client.audio.transcriptions.create({ model: 'gpt-4o-transcribe', file: fs.createReadStream(process.env.AUDIO_FILE || '{{audio_file}}'), translate: true });
console.log(resp.text);
