import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const inputs = ['hello', 'fluorite', 'mcp'];
const out = await client.embeddings.create({ model: '{{openai_embed_model}}', input: inputs });
console.log(out.data.map(d => d.embedding.length));
