import OpenAI from 'openai';
import express from 'express';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const app = express();
app.get('/time', (_req, res) => { res.json({ now: new Date().toISOString() }); });
app.listen(3000, async () => {
  console.log('Server on http://localhost:3000');
  const response = await client.chat.completions.create({
    model: '{{openai_model}}',
    messages: [{ role: 'user', content: 'What time is it? Use the tool.' }],
    tools: [{
      type: 'function',
      function: { name: 'get_time', description: 'Get the current time', parameters: { type: 'object', properties: {}, required: [] } }
    }]
  });
  console.log(JSON.stringify(response, null, 2));
});
