import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const tools = [
  { type: 'function', function: { name: 'get_time', description: 'Get current time', parameters: { type: 'object', properties: {} } } },
  { type: 'function', function: { name: 'get_date', description: 'Get current date', parameters: { type: 'object', properties: {} } } }
];
const res = await client.chat.completions.create({ model: '{{openai_model}}', messages: [{ role: 'user', content: 'What time is it today? Use a tool if needed.' }], tools });
console.log(JSON.stringify(res, null, 2));
