import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const tools = [{ type: 'function', function: { name: 'get_time', description: 'Get current time', parameters: { type: 'object', properties: {} } } }];
const m1 = await client.chat.completions.create({ model: '{{openai_model}}', messages: [{ role: 'user', content: 'What time is it? Use the tool.' }], tools });
const m2 = await client.chat.completions.create({ model: '{{openai_model}}', messages: [{ role: 'user', content: 'What time is it? Use the tool.' }, { role: 'tool', content: '12:34', name: 'get_time' }], tools });
console.log(m2.choices?.[0]?.message?.content);
