import OpenAI from 'openai';
import fs from 'node:fs';
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
// Requires input.png and mask.png present in the folder
const img = await openai.images.edits({ prompt: process.env.PROMPT || '{{prompt}}', image: fs.createReadStream('input.png'), mask: fs.createReadStream('mask.png'), size: '512x512' });
console.log(img.data?.[0]?.url);
