import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const schema = { name: 'Color', schema: { type: 'object', properties: { color: { type: 'string', enum: ['red','green','blue'] } }, required: ['color'] } };
const res = await client.chat.completions.create({ model: '{{openai_model}}', response_format: { type: 'json_schema', json_schema: schema }, messages: [{ role: 'user', content: 'Return color green' }] });
console.log(res.choices?.[0]?.message?.content);
