import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const jsonSchema = { name: 'PersonList', schema: { type: 'object', properties: { people: { type: 'array', items: { type: 'object', properties: { name: { type: 'string' }, age: { type: 'number' } }, required: ['name'] } } }, required: ['people'] } };
const res = await client.chat.completions.create({ model: '{{openai_model}}', response_format: { type: 'json_schema', json_schema: jsonSchema }, messages: [{ role: 'user', content: 'Return two people named Alice(30) and Bob(25).' }] });
console.log(res.choices?.[0]?.message?.content);
