import OpenAI from 'openai';
const client = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const schema = { name: 'Shape', schema: { oneOf: [ { type: 'object', properties: { type: { const: 'circle' }, radius: { type: 'number' } }, required: ['type','radius'] }, { type: 'object', properties: { type: { const: 'square' }, size: { type: 'number' } }, required: ['type','size'] } ] } };
const res = await client.chat.completions.create({ model: '{{openai_model}}', response_format: { type: 'json_schema', json_schema: schema }, messages: [{ role: 'user', content: 'Return a circle with radius 3 as JSON' }] });
console.log(res.choices?.[0]?.message?.content);
