const express = require('express');
const OpenAPIBackend = require('openapi-backend').default;
const api = new OpenAPIBackend({ definition: require('./spec.json') });
api.register('hello', (c, req, res)=> res.json({ message: 'hello' }));
api.init();
const app = express();
app.use((req,res)=> api.handleRequest(req, req, res));
app.listen(3000);
