const express = require('express');
const swaggerUi = require('swagger-ui-express');
const spec = require('./openapi.json');
const app = express();
app.use('/docs', swaggerUi.serve, swaggerUi.setup(spec));
app.get('/hello', (_req, res) => res.json({ message: 'hello' }));
app.listen(3000);
