const { Client } = require('@opensearch-project/opensearch');
const client = new Client({ node: process.env.OS_URL || 'http://localhost:9200' });
(async()=>{ const ops=[{ index: { _index: process.env.OS_INDEX || '{{index}}' } }, { id: 1, title: 'Hello' }]; console.log(await client.bulk({ refresh: true, body: ops })); })();
