import { Client } from '@opensearch-project/opensearch';
const client = new Client({ node: process.env.OS_NODE || '{{os_node}}' });
const index = 'docs';
try { await client.indices.create({ index }); } catch {}
await client.index({ index, body: { text: 'hello', tag: 'a' } });
await client.indices.refresh({ index });
const del = await client.deleteByQuery({ index, body: { query: { term: { tag: 'a' } } } });
console.log(del.body?.deleted || 0);
