import { Client } from '@opensearch-project/opensearch';
const client = new Client({ node: process.env.OS_NODE || '{{os_node}}' });
try { const resp = await client.indices.delete({ index: process.env.OS_INDEX || '{{index_name}}' }); console.log(resp.body); } catch (e) { console.log('Delete failed', e?.message); }
