import { Client } from '@opensearch-project/opensearch';
const client = new Client({ node: process.env.OS_NODE || '{{os_node}}' });
const index = 'docs';
try { await client.indices.delete({ index }); } catch {}
await client.indices.create({ index, body: { settings: { index: { knn: true } }, mappings: { properties: { text: { type: 'text' }, embedding: { type: 'knn_vector', dimension: 3, method: { name: 'hnsw', space_type: 'l2', engine: 'nmslib' } } } } } });
await client.index({ index, body: { text: 'hello world', embedding: [0.1,0.2,0.3] } });
await client.index({ index, body: { text: 'fluorite', embedding: [0.2,0.1,0.25] } });
await client.indices.refresh({ index });
const res = await client.search({ index, body: { knn: { field: 'embedding', query_vector: [0.1,0.2,0.3], k: 2, num_candidates: 10 }, _source: ['text'] } });
console.log(res.body.hits?.hits?.map(h => h._source?.text));
